        public bool IsValid(string s)
        {
            var stack = new Stack<char>();
            for (int i = 0; i < s.Length; i++)
            {
                if (s[i] == '(')
                {
                    stack.Push(')');
                }
                else if (s[i] == '{')
                {
                    stack.Push('}');
                }
                else if (s[i] == '[')
                {
                    stack.Push(']');
                }
                else if (s[i] == ')' && stack.Count > 0 && stack.Peek() == ')')
                {
                    stack.Pop();
                }
                else if (s[i] == '}' && stack.Count > 0 && stack.Peek() == '}')
                {
                    stack.Pop();
                }
                else if (s[i] == ']' && stack.Count > 0 && stack.Peek() == ']')
                {
                    stack.Pop();
                }
                else
                {
                    return false;
                }
            }
            return stack.Count == 0 ? true : false;
